import time
import re
import os
import pandas as pd
from selenium import webdriver
from selenium.webdriver.common.by import By
from selenium.webdriver.support.ui import WebDriverWait
from selenium.webdriver.support import expected_conditions as EC
from selenium.common.exceptions import TimeoutException, NoSuchElementException


def init_driver():
    # Initiate the driver
    options = webdriver.ChromeOptions()
    options.headless = True
    driver = webdriver.Chrome(options=options)
    driver.wait = WebDriverWait(driver, 10)
    
    return driver

def read_files(dir):
    # Read the local file names into a query queue to use at Goodreads
    queue = [f.replace('.txt', '') for f in os.listdir(dir) 
            if f.endswith('.txt')]

    print(queue)

    return queue

def send_requests(queue, driver):
    # Get the list of urls at Goodreads for the previously acquired books
    result = []

    for q in queue:
        try:
            # The traditional query-keyword method kept failing so I tried something different(string-oriented)
            list = q.split(' ')
            query = '+'.join(list)
            driver.get('https://www.goodreads.com/search?q={}'.format(query))

            link = driver.wait.until(EC.presence_of_element_located(
                    (By.CLASS_NAME, 'bookTitle')))
            result.append((q, link.get_attribute('href')))
        
        except:
            print('Error.')
            pass
    
    print(result)

    return result

def get_books(queue, driver):
    # Scrap some informative data at each Goodreads page
    header = ['name', 'synopsis', 'genres']
    results = pd.DataFrame(columns=header)
    
    for q in queue:
        try:
            driver.get(q[1])

            # Get name and synopis of the book
            name = q[0]
        
            content = driver.wait.until(EC.presence_of_element_located(
                                (By.XPATH, "//*[contains(@id, 'freeText')][not(contains(@id, 'Container'))]")))
            # Display the element    
            driver.execute_script("arguments[0].style.display = 'block';", content)
            content = content.text

            print(content) 

            # Get the respective genres of the book
            genre_link = driver.find_element_by_link_text('See top shelves…')
            print(genre_link.get_attribute('href'))
            time.sleep(2)
            genre_link.click()
            genres = driver.wait.until(EC.presence_of_all_elements_located(
                    (By.CSS_SELECTOR, ".mediumText.actionLinkLite")))

            results = results.append({'name':name, 'synopsis':content,
                                    'genres':[g.text for g in genres]}, ignore_index=True)

            results.to_csv('./GoodReads_information.csv',
                            header=None, index=None, sep=' ', mode='a')
        
        except:
            print('Failed')    
            pass

    return results

def main():
        driver = init_driver()
        queue = read_files('./gutenberg')
        list = send_requests(queue, driver)
        print(get_books(list, driver))
        driver.close()

main()







